mapper.select:
select * from node where status = 1
and name like concat('%',#{name},'%')
and level = #{level}

select n.id,n.name,n.level,n.status,n.properties,u1.name as create_u,n.create_time,n.modify_time from node n,user u1 where n.create_user = u1.id and n.status = 1
and n.name like concat('%',#{name},'%')
and n.level = #{level}

mapper.update:
update node
set status = 1
where id = #{id}

update node
set status = 2
where id = #{id}
